  trial:     { bg:'rgba(26,58,107,0.12)',   color:'#1B3A6B', border:'rgba(26,58,107,0.3)',    label:'Es' },
  cancelled: { bg:'rgba(100,116,139,0.12)', color:'#64748b', border:'rgba(100,116,139,0.3)', label:'Anile' },
